    // animateElements('.slide-in-left', { x: -50, opacity: 0, duration: 1 });
    // animateElements('.slide-in-right', { x: 50, opacity: 0, duration: 1 });
  }, []);

  return (
    <div className="App">
      <Header />
      <main>
        <Hero />
        <AudioPlayer />
        <About />
        <Experience />
        <Projects />
        <Skills />
        <Education />
        <Contact />
      </main>
      <Footer />
    </div>
  );
}

export default App;
