import React, { useEffect } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import ReactGA from 'react-ga';
import './'; // Make sure this path is correct

import Header from './components/Header';
import Hero from './components/Hero';
import About from './components/About';
import Experience from './components/Experience';
import Projects from './components/Projects';
import Skills from './components/Skills';
import Education from './components/Education';
import Contact from './components/Contact';
import Footer from './components/Footer';
import AudioPlayer from './components/AudioPlayer';

gsap.registerPlugin(ScrollTrigger);

// Initialize Google Analytics
ReactGA.initialize('YOUR_GA_TRACKING_ID');

function App() {
  useEffect(() => {
    // Page view tracking
    ReactGA.pageview(window.location.pathname + window.location.search);

    // GSAP animations setup
    const animateElements = (selector, props) => {
      gsap.utils.toArray(selector).forEach(element => {
        gsap.from(element, {
          ...props,
          scrollTrigger: {
            trigger: element,
            start: 'top 80%',
          }
        });
      });
    };

    animateElements('.fade-in', { opacity: 0, duration: 1 });
    animateElements('.slide-up', { y: 50, opacity: 0, duration: 1 });
    animateElements('.slide-in-left', { x: -50, opacity: 0, duration: 1 });
    animateElements('.slide-in-right', { x: 50, opacity: 0, duration: 1 });

  }, []);

  return (
    <div className="App">
      <Header className="fade-in" />
      <Hero className="slide-up" />
      <About className="fade-in" />
      <Experience className="slide-in-left" />
      <Projects className="slide-in-right" />
      <Skills className="fade-in" />
      <Education className="slide-up" />
      <Contact className="fade-in" />
      <Footer className="slide-up" />
      <AudioPlayer />
    </div>
  );
}

export default App;
